package java_Beginner;

public class DataTypes {

	//Types of Datatype - Primitive & Non-Primitive
	//Primitive (stores the data of only one type) - byte, short, int, long, float, double, boolean & char
	//Non-primitive ( store the data of more than one type) - String, Array, Class, Interface
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name = "Vinoth";
		System.out.println(name);
		byte age = 25;
		System.out.println(age);
		float weight =  65.5F;
		double height =  165.561614916911;
		long phoneNumber = 98959895989595L;
		int number =  156488484;
		short salary = 10000;
		char initial = 'K';
		boolean liked = true;
		
		System.out.println(weight + height + phoneNumber + number + salary + initial);
		
	}

}
